package ch4;

public class For5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j;
		for(i=2; i<10; i++) {										// 단
			for(j=1; j<10; j++) {									// 곱해지는 수
				System.out.println(i+ " * " + j + " = " + (i*j));
			}
			System.out.println();
		}
	}

}
                                              